import React, { useEffect } from "react";

const AdMaxSidebar = () => {
    useEffect(() => {
        const script = document.createElement("script");
        script.src = "https://adm.shinobi.jp/s/ed30413d8ef4760b707dc1168527491d";
        script.async = true;
        document.getElementById("ad-sidebar-container").appendChild(script);
    }, []);

    return (
        <div style={{
            position: "fixed",
            top: "100px",
            right: "10px",
            width: "160px",
            height: "600px",
            backgroundColor: "#f4f4f4",
            border: "1px solid #ccc",
            display: "flex",
            justifyContent: "center",
            alignItems: "center"
        }}>
            <div id="ad-sidebar-container"></div>
        </div>
    );
};

export default AdMaxSidebar;
