import { useCallback, createElement } from 'react';

function PostScribe({ html, afterAsync, afterDequeue, afterStreamStart, afterWrite, done, autoFix, beforeEnqueue, beforeWrite, beforeWriteToken, error, releaseAsync, }) {
    const handleMount = useCallback((element) => {
        if (typeof window === "undefined" || !element) {
            return;
        }
        import('postscribe').then((postscribe) => {
            postscribe.default(element, html, {
                afterAsync,
                afterDequeue,
                afterStreamStart,
                afterWrite,
                done,
                autoFix,
                beforeEnqueue,
                beforeWrite,
                beforeWriteToken,
                error,
                releaseAsync,
            });
        });
    }, []);
    return createElement("div", { ref: handleMount });
}

export { PostScribe as default };
