import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { TextField, Button, Card, CardMedia, CardContent, Typography, Grid, Dialog, DialogTitle, DialogContent } from "@mui/material";

const DMMSearch = () => {
    const [keyword, setKeyword] = useState("");
    const [actresses, setActresses] = useState([]);
    const [selectedImage, setSelectedImage] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        // おすすめ女優リストの取得 (publicフォルダの画像を使用)
        setActresses([
            { name: "波多野結衣", image: "/resources/yui_hatano.jpg" },
            { name: "麻美ゆま", image: "/resources/yuma_asami.jpg" }
        ]);
    }, []);

    const handleSearch = () => {
        if (!keyword.trim()) return;
        navigate(`/productlist?keyword=${encodeURIComponent(keyword)}`);
    };

    const handleActressSearch = (actressName) => {
        navigate(`/productlist?keyword=${encodeURIComponent(actressName)}`);
    };

    return (
        <div style={{ padding: "20px", maxWidth: "900px", margin: "auto" }}>
            <Typography variant="h4" gutterBottom>DMM 商品検索</Typography>
            <TextField
                fullWidth
                label="キーワードを入力"
                variant="outlined"
                value={keyword}
                onChange={(e) => setKeyword(e.target.value)}
                style={{ marginBottom: "10px" }}
            />
            <Button variant="contained" color="primary" onClick={handleSearch} fullWidth>
                検索
            </Button>

            {/* おすすめ女優リスト */}
            <Typography variant="h6" style={{ marginTop: "20px" }}>おすすめ女優</Typography>
            <Grid container spacing={2}>
                {actresses.map((actress, index) => (
                    <Grid item xs={6} sm={3} key={index}>
                        <Card 
                            onClick={() => handleActressSearch(actress.name)}
                            style={{
                                cursor: "pointer",
                                width: "100%", // カードの幅を統一
                                maxWidth: "200px", // 最大幅を設定
                                textAlign: "center"
                            }}
                        >
                            <CardMedia
                                component="img"
                                image={actress.image}
                                alt={actress.name}
                                style={{
                                    height: "200px", // 画像の高さを統一
                                    objectFit: "cover" // 画像をトリミングせず比率を保つ
                                }}
                            />
                            <CardContent>
                                <Typography variant="body1">{actress.name}</Typography>
                            </CardContent>
                        </Card>
                    </Grid>
                ))}
            </Grid>

            {/* 画像拡大用ダイアログ */}
            <Dialog open={Boolean(selectedImage)} onClose={() => setSelectedImage(null)}>
                <DialogTitle>拡大画像</DialogTitle>
                <DialogContent>
                    <img src={selectedImage} alt="拡大画像" style={{ width: "100%" }} />
                </DialogContent>
            </Dialog>
        </div>
    );
};

export default DMMSearch;
