import PostScribe from "@marshallku/react-postscribe";

export const AdMaxSidebar = ({ adId }) => {
  useEffect(() => {
    // 広告IDの設定（グローバル変数に直接代入する場合）
    window.admaxads = window.admaxads || [];
    if (!window.admaxads.some(ad => ad.admax_id === adId)) {
      window.admaxads.push({ admax_id: adId, type: 'banner' });
    }
  }, [adId]);

  // PostScribeコンポーネントでスクリプトを挿入
  return (
    <div className="admax-ads" data-admax-id={adId}>
      <PostScribe html={`<script src="https://adm.shinobi.jp/st/t.js" async></script>`} />
    </div>
  );
};

export default AdMaxSidebar;
