import React, { useEffect, useState } from "react";

const AdMaxSidebar = ({ pcAdId, mobileAdId }) => {
    const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

    useEffect(() => {
        const handleResize = () => {
            setIsMobile(window.innerWidth <= 768);
        };

        window.addEventListener("resize", handleResize);
        handleResize();

        return () => window.removeEventListener("resize", handleResize);
    }, []);

    useEffect(() => {
        const adId = isMobile ? mobileAdId : pcAdId;

        // 既存の広告をすべて削除
        document.querySelectorAll(".admax-ads").forEach(el => el.remove());

        // `window.admaxads` を新しく設定（毎回リセットして上書き）
        window.admaxads = [{ admax_id: adId, type: "banner" }];

        // 古いスクリプトを削除
        document.querySelectorAll("script[src='https://adm.shinobi.jp/st/t.js']").forEach(el => el.remove());

        // 新しいスクリプトを追加
        const script = document.createElement("script");
        script.src = "https://adm.shinobi.jp/st/t.js";
        script.async = true;
        document.body.appendChild(script);

        return () => {
            script.remove();
            document.querySelectorAll(".admax-ads").forEach(el => el.remove());
        };
    }, [isMobile, pcAdId, mobileAdId]);

    return (
        <div
            key={isMobile ? mobileAdId : pcAdId} // 強制的にコンポーネントを再レンダリング
            style={{
                position: "fixed",
                bottom: isMobile ? "0px" : "auto",
                top: isMobile ? "auto" : "100px",
                right: isMobile ? "0px" : "10px",
                left: isMobile ? "0px" : "auto",
                width: isMobile ? "100%" : "160px",
                height: isMobile ? "50px" : "600px",
                backgroundColor: "#f4f4f4",
                border: "1px solid #ccc",
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                zIndex: 1000,
            }}
        >
            <div className="admax-ads" data-admax-id={isMobile ? mobileAdId : pcAdId}></div>
        </div>
    );
};

export default AdMaxSidebar;
