import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

const ProductPage = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const product = location.state?.product;

    if (!product) {
        return <div>商品情報が見つかりません。</div>;
    }

    return (
        <div>
            <h1>{product.title}</h1>
            <button onClick={() => navigate(-1)}>戻る</button>
            <div>
                <img src={product.image} alt={product.title} width="300" />
            </div>
            <p><strong>メーカー:</strong> {product.maker}</p>
            <p><strong>発売日:</strong> {product.release_date}</p>
            <p><strong>女優:</strong> {product.actress.join(", ")}</p>
            <p><strong>評価:</strong> {product.review_average}</p>
            <a href={product.affiliate_url} target="_blank" rel="noopener noreferrer">
                <button>購入ページへ</button>
            </a>
        </div>
    );
};

export default ProductPage;
