import React, { useEffect, useRef } from "react";

const AdmaxAd = () => {
    const adContainerRef = useRef(null);

    useEffect(() => {
        if (!adContainerRef.current) return;

        // すでにスクリプトがある場合は追加しない
        if (document.getElementById("admax-script")) return;

        const script = document.createElement("script");
        script.id = "admax-script";
        script.src = "https://adm.shinobi.jp/s/8ff1b1ebeb541e700b8323afb1d20756";
        script.async = true;
        adContainerRef.current.appendChild(script);

        return () => {
            // コンポーネントがアンマウントされたらスクリプトを削除
            adContainerRef.current?.removeChild(script);
        };
    }, []);

    return (
        <div ref={adContainerRef} style={{ textAlign: "center", margin: "20px 0" }}>
            <p style={{ fontSize: "12px", color: "#999" }}>スポンサーリンク</p>
            <div id="admax-container"></div>
        </div>
    );
};

export default AdmaxAd;
