import React, { useEffect, useState } from "react";

const GOOGLE_API_KEY = process.env.REACT_APP_GOOGLE_API_KEY;
const CX = process.env.REACT_APP_GOOGLE_CX;

const GoogleImageSearch = ({ actressName }) => {
  const [imageUrl, setImageUrl] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchImage = async () => {
      setLoading(true);
      try {
        const searchString = actressName + " dmm av女優"
        const response = await fetch(
          `https://www.googleapis.com/customsearch/v1?q=${encodeURIComponent(searchString)}&cx=${CX}&key=${GOOGLE_API_KEY}&searchType=image&num=1`
        );
        const data = await response.json();

        if (data.items && data.items.length > 0) {
          setImageUrl(data.items[0].link);
        } else {
          setImageUrl(null);
        }
      } catch (error) {
        console.error("Error fetching image:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchImage();
  }, [actressName]);

  return (
    <div style={{ textAlign: "center", width: "100%", maxWidth: "1000px", margin: "auto" }}>
      {loading ? (
        <p>画像を検索中...</p>
      ) : imageUrl ? (
        <img
          src={imageUrl}
          alt={actressName}
          style={{ width: "100%", height: "auto", objectFit: "contain", borderRadius: "8px" }}
        />
      ) : (
        <p>画像が見つかりませんでした。</p>
      )}
    </div>
  );
};

export default GoogleImageSearch;
