import React, { useEffect } from 'react';

const AdMax = ({ admaxId }) => {
  useEffect(() => {
    // グローバル変数admaxadsが存在しない場合は初期化
    window.admaxads = window.admaxads || [];

    // admaxIdが既に登録されていない場合のみ追加
    if (!window.admaxads.some(ad => ad.admax_id === admaxId)) {
      window.admaxads.push({
        admax_id: admaxId,
        type: 'switch',
      });
    }

    // スクリプトタグの作成と追加
    const script = document.createElement('script');
    script.src = 'https://adm.shinobi.jp/st/t.js';
    script.async = true;
    document.body.appendChild(script);

    // クリーンアップ関数：コンポーネントのアンマウント時に実行
    return () => {
      // スクリプトタグを削除
      document.body.removeChild(script);

      // admaxadsから該当の広告を削除
      window.admaxads = window.admaxads.filter(ad => ad.admax_id !== admaxId);

      // グローバル変数をクリア
      window.__admax_tag__ = undefined;
    };
  }, [admaxId]);

  // 広告表示用のdivを返す
  return (
    <div
      className="admax-switch"
      data-admax-id={admaxId}
      style={{ display: 'inline-block' }}
    />
  );
};

export default AdMax;
