import React from "react";
import { Link } from "react-router-dom";
import { Grid, Card, CardMedia, CardContent, Typography, Button } from "@mui/material";

const ActressList = ({ actresses }) => {
  if (!actresses || actresses.length === 0) {
    return <Typography sx={{ textAlign: "center", marginTop: 4, color: "white", fontSize: "1.5rem" }}>
      検索結果がありません。
    </Typography>;
  }

  return (
    <Grid container spacing={3} className="grid-container">
      {actresses.map((actress) => {
        const imageUrl = actress.imageURL?.small || "https://via.placeholder.com/150";

        return (
          <Grid item xs={12} sm={6} md={4} key={actress.id}>
            <Card className="card">
              <CardMedia
                component="img"
                height="220"
                image={imageUrl}
                alt={actress.name}
                sx={{ objectFit: "cover", borderRadius: "8px" }}
              />
              <CardContent>
                <Typography variant="h6" sx={{ fontWeight: "bold" }}>{actress.name}</Typography>
                <Button
                  component={Link}
                  to={`/actress/${actress.id}`}
                  variant="contained"
                  color="primary"
                  sx={{ marginTop: 1 }}
                >
                  詳細を見る
                </Button>
              </CardContent>
            </Card>
          </Grid>
        );
      })}
    </Grid>
  );
};

export default ActressList;
