import React, { useEffect } from "react";

const AdmaxAd = () => {
    useEffect(() => {
        const script = document.createElement("script");
        script.src = "https://adm.shinobi.jp/s/8ff1b1ebeb541e700b8323afb1d20756";
        script.async = true;
        script.id = "admax-script";

        // すでにスクリプトが追加されていない場合のみ追加
        if (!document.getElementById("admax-script")) {
            document.body.appendChild(script);
        }

        return () => {
            // アンマウント時に削除しない（広告を保持）
        };
    }, []);

    return (
        <div style={{ textAlign: "center", margin: "20px 0" }}>
            <p style={{ fontSize: "12px", color: "#999" }}>スポンサーリンク</p>
            {/* Admax 用の div を設置 */}
            <div id="admixer-container">
                <iframe
                    title="admax-ad"
                    src="https://adm.shinobi.jp/s/8ff1b1ebeb541e700b8323afb1d20756"
                    width="100%"
                    height="250"
                    frameBorder="0"
                    scrolling="no"
                ></iframe>
            </div>
        </div>
    );
};

export default AdmaxAd;
