import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { Card, CardMedia, CardContent, Typography, Grid, Button } from "@mui/material";

const ProductList = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const queryParams = new URLSearchParams(location.search);
    const keyword = queryParams.get("keyword");

    const [items, setItems] = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        if (!keyword) return;

        const fetchProducts = async () => {
            setLoading(true);
            const response = await fetch(`https://73f92o14r4.execute-api.ap-northeast-1.amazonaws.com/dev/nostalgic?keyword=${keyword}`);
            const data = await response.json();
            setItems(data.items);
            setLoading(false);
        };

        fetchProducts();
    }, [keyword]);

    return (
        <div style={{ padding: "20px", maxWidth: "900px", margin: "auto" }}>
            <Button variant="contained" color="secondary" onClick={() => navigate("/")}>
                ホームへ戻る
            </Button>
            <Typography variant="h4" gutterBottom>検索結果: {keyword}</Typography>

            {loading && <Typography>検索中...</Typography>}

            <Grid container spacing={2}>
                {items.map((item) => (
                    <Grid item xs={12} sm={6} md={4} key={item.affiliate_url}>
                        <Card 
                            onClick={() => navigate(`/product`, { state: { product: item } })}
                            style={{ cursor: "pointer", transition: "transform 0.2s, box-shadow 0.2s" }}
                            onMouseOver={(e) => { e.currentTarget.style.transform = "scale(1.05)"; e.currentTarget.style.boxShadow = "0px 4px 10px rgba(0,0,0,0.2)"; }}
                            onMouseOut={(e) => { e.currentTarget.style.transform = "scale(1)"; e.currentTarget.style.boxShadow = "none"; }}
                        >
                            <CardMedia
                                component="img"
                                height="200"
                                image={item.image}
                                alt={item.title}
                            />
                            <CardContent>
                                <Typography variant="body1">{item.title}</Typography>
                                <Typography variant="body2" color="textSecondary">{item.maker}</Typography>
                            </CardContent>
                        </Card>
                    </Grid>
                ))}
            </Grid>
        </div>
    );
};

export default ProductList;
