import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import { AppBar, Toolbar, Typography, Container, Box } from "@mui/material";
import DMMSearch from "./components/DMMSearch";
import ActressList from "./components/ActressList";
import ActressPage from "./components/ActressPage";
import Breadcrumbs from "./components/Breadcrumbs";
import "./styles/GlobalStyles.css"; // グローバルCSSを適用

const App = () => {
  const [actresses, setActresses] = useState([]);
  const [keyword, setKeyword] = useState("");

  useEffect(() => {
    if (!keyword) return;

    fetch(`https://73f92o14r4.execute-api.ap-northeast-1.amazonaws.com/dev/search?keyword=${encodeURIComponent(keyword)}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.items) {
          // 🔹 画像 (`imageURL.small`) がある女優のみをセット
          const filteredActresses = data.items.filter(actress => actress.imageURL?.small);
          setActresses(filteredActresses);
        } else {
          setActresses([]);
        }
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setActresses([]);
      });
  }, [keyword]);

  return (
    <Router>
      <Box className="app-container">
        {/* 🔹 ヘッダー */}
        <AppBar position="static" className="header">
          <Toolbar>
            <Typography variant="h6" sx={{ fontWeight: "bold" }}>
              Actress Search
            </Typography>
          </Toolbar>
        </AppBar>

        {/* 🔹 検索窓（上部固定） */}
        <DMMSearch setKeyword={setKeyword} />

        {/* 🔹 メインコンテンツ */}
        <Container sx={{ flexGrow: 1, marginTop: 5 }}>
          {/* 🔹 パンクズリスト */}
          <Breadcrumbs />

          <Routes>
            <Route path="/" element={<ActressList actresses={actresses} />} />
            <Route path="/actress/:id" element={<ActressPage actresses={actresses} />} />
          </Routes>
        </Container>
      </Box>
    </Router>
  );
};

export default App;
