import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const DMMSearch = () => {
    const [keyword, setKeyword] = useState("");
    const [items, setItems] = useState([]);
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleSearch = async () => {
        if (!keyword.trim()) return;
        setLoading(true);

        const response = await fetch(`https://73f92o14r4.execute-api.ap-northeast-1.amazonaws.com/dev/nostalgic?keyword=${keyword}`);
        const data = await response.json();

        setItems(data.items);
        setLoading(false);
    };

    return (
        <div>
            <h1>DMM 商品検索</h1>
            <input
                type="text"
                value={keyword}
                onChange={(e) => setKeyword(e.target.value)}
                placeholder="キーワードを入力"
            />
            <button onClick={handleSearch} disabled={loading}>
                {loading ? "検索中..." : "検索"}
            </button>

            <ul>
                {items.map((item) => (
                    <li key={item.affiliate_url} onClick={() => navigate(`/product`, { state: { product: item } })}>
                        <img src={item.image} alt={item.title} width="100" />
                        <p>{item.title}</p>
                        <p>メーカー: {item.maker}</p>
                        <p>発売日: {item.release_date}</p>
                        <p>女優: {item.actress.join(", ")}</p>
                        <p>評価: {item.review_average}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default DMMSearch;
