import React, { useEffect } from "react";

const AdMaxSidebar = ({ adId }) => {
    useEffect(() => {
        // 既存の広告スクリプトを削除（Reactの再レンダリング時にリセットする）
        const existingScripts = document.querySelectorAll(`script[src*="adm.shinobi.jp"]`);
        existingScripts.forEach(script => script.remove());

        // 新しいスクリプトを作成
        const script = document.createElement("script");
        script.src = `https://adm.shinobi.jp/s/${adId}`;
        script.async = true;
        script.defer = true;
        document.body.appendChild(script);

        return () => {
            document.body.removeChild(script);
        };
    }, [adId]);

    return (
        <div style={{
            position: "fixed",
            top: "100px",
            right: "10px",
            width: "160px",
            height: "600px",
            backgroundColor: "#f4f4f4",
            border: "1px solid #ccc",
            display: "flex",
            justifyContent: "center",
            alignItems: "center"
        }}>
            <div className="admax-ads" data-admax-id={adId}></div>
        </div>
    );
};

export default AdMaxSidebar;
