import React, { useEffect, useRef } from "react";

const AdmaxAd = () => {
    const adRef = useRef(null);

    useEffect(() => {
        if (adRef.current) {
            adRef.current.innerHTML = `
                <script async="async" src="https://adm.shinobi.jp/s/dc1f16d01b6d9e6b34c5474923361e51"></script>
            `;
        }
    }, []);

    return (
        <div style={{ textAlign: "center", margin: "20px 0" }}>
            <p style={{ fontSize: "12px", color: "#999" }}>スポンサーリンク</p>
            <div ref={adRef}></div>
        </div>
    );
};

export default AdmaxAd;
