import React, { useEffect } from "react";

const AdMaxSidebar = ({ adId }) => {
    useEffect(() => {
        window.admaxads = window.admaxads || [];
        if (!window.admaxads.some(ad => ad.admax_id === adId)) {
            window.admaxads.push({ admax_id: adId, type: "banner" });
        }

        const script = document.createElement("script");
        script.src = "https://adm.shinobi.jp/st/t.js";
        script.async = true;
        document.body.appendChild(script);

        return () => {
            document.body.removeChild(script);
        };
    }, [adId]);

    return (
        <div style={{
            position: "fixed",
            top: "100px",
            right: "10px",
            width: "160px",
            height: "600px",
            backgroundColor: "#f4f4f4",
            border: "1px solid #ccc",
            display: "flex",
            justifyContent: "center",
            alignItems: "center"
        }}>
            <div className="admax-ads" data-admax-id={adId}></div>
        </div>
    );
};

export default AdMaxSidebar;
